mapper.select:
		select count(*) from
		t_log l
		left join t_admin a
		on l.aid = a.aid


				and a.aname = #{aname}


				and l.ltype = #{ltype}


				and l.lmethod = #{lmethod}


 				and l.loperateDate > #{loperateDate}


 				and l.ltimeout < #{ltimeout}

		select l.*, a.aname from
		t_log l
		left join t_admin a
		on l.aid = a.aid


				and a.aname = #{aname}


				and l.ltype = #{ltype}


				and l.lmethod = #{lmethod}


 				and l.loperateDate > #{loperateDate}


 				and l.ltimeout < #{ltimeout}


		order by  #{sort}
		#{sortOrder}
		limit #{offset}, #{ps}
