mapper.select:
select * from t_house


		and hid = #{hid}


		and hname = #{hname}


		and hsubway LIKE '%${hsubway}%'


		and hmodel LIKE '%${hmodel}%'


		and htype = #{htype}


		and hmethod = #{hmethod}


		and hposition LIKE '%${hposition}%'


		and hprice >= #{lprice}


		and hprice < #{hpric}


		and hid LIKE '%浦东%' || hname LIKE '%浦东%'
		|| hsubway LIKE
		'%浦东%' || hmodel LIKE '%浦东%'
		|| htype LIKE '%浦东%' || hmethod LIKE
		'%浦东%' || hposition LIKE '%浦东%'


		and hstate = '0'


ORDER BY hprice asc
limit #{x}, #{y}

select count(*) from t_house


		and hid = #{hid}


		and hname = #{hname}


		and hsubway LIKE '%${hsubway}%'


		and hmodel LIKE '%${hmodel}%'


		and htype = #{htype}


		and hmethod = #{hmethod}


		and hposition LIKE '%${hposition}%'


		and hprice >= #{lprice}


		and hprice < #{hpric}


		and hid LIKE '%浦东%' || hname LIKE '%浦东%'
		|| hsubway LIKE
		'%浦东%' || hmodel LIKE '%浦东%'
		|| htype LIKE '%浦东%' || hmethod LIKE
		'%浦东%' || hposition LIKE '%浦东%'


		and hstate = '0'


ORDER BY hprice asc
